-- Create the products table
CREATE TABLE products (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    price INT NOT NULL,
    image_url VARCHAR(255),
    ai_hint VARCHAR(255),
    href VARCHAR(255) UNIQUE,
    category VARCHAR(50) CHECK(category IN ('pc', 'ios', 'android'))
);

